mapper.select:
SELECT id, username, telno, pwd, create_date FROM user_info


        id = #{id}


        AND username = #{username}


        AND telno = #{telno}


        and pwd = #{pwd}

mapper.update:
UPDATE user_info


        error_count = #{error_count},


        status = #{status},


        pwd = #{pwd}

        modify_date = now(),

WHERE id = #{id};
